import React, { useState, useRef, useEffect } from 'react';
import './UVLight.css';

const UVLight = () => {
  const [isUVOn, setIsUVOn] = useState(false);
  const [cameraStream, setCameraStream] = useState(null);
  const [selectedDenomination, setSelectedDenomination] = useState('‚Ç±100');
  const [intensity, setIntensity] = useState(75);
  const videoRef = useRef(null);

  useEffect(() => {
    const stopCameraCleanup = () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
    };

    startCamera();
    return stopCameraCleanup;
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      setCameraStream(stream);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  const toggleUV = () => {
    setIsUVOn(!isUVOn);
  };

  const securityFeatures = {
    '‚Ç±20': { color: '#6366f1', features: ['Security thread glows blue', 'Portrait watermark visible'] },
    '‚Ç±50': { color: '#10b981', features: ['Security thread glows green', 'Hidden denomination appears'] },
    '‚Ç±100': { color: '#ef4444', features: ['Security thread glows red', 'UV-reactive fibers visible'] },
    '‚Ç±200': { color: '#f59e0b', features: ['Security thread glows orange', 'Special ink patterns'] },
    '‚Ç±500': { color: '#eab308', features: ['Security thread glows yellow', 'Complex UV patterns'] },
    '‚Ç±1000': { color: '#8b5cf6', features: ['Multi-color security thread', 'Advanced UV features'] }
  };

  return (
    <div className="uv-container">
      {/* Header */}
      <header className="uv-header">
        <div className="header-content">
          <div className="title-section">
            <h1>UV Light Detection</h1>
            <p>Professional UV authentication for Philippine peso bills</p>
          </div>
          <div className="status-section">
            <div className={`uv-status ${isUVOn ? 'active' : 'inactive'}`}>
              <div className="status-indicator"></div>
              <span>{isUVOn ? 'UV Active' : 'UV Inactive'}</span>
            </div>
          </div>
        </div>
      </header>

      <div className="uv-main">
        {/* Camera Section */}
        <div className="camera-section">
          <div className={`camera-viewport ${isUVOn ? 'uv-mode' : ''}`}>
            <video
              ref={videoRef}
              autoPlay
              playsInline
              className="camera-feed"
            />
            
            {isUVOn && (
              <div className="uv-overlay">
                <div className="uv-filter"></div>
                <div className="scan-pattern"></div>
              </div>
            )}

            {/* Floating Controls */}
            <div className="camera-controls">
              <button
                className={`uv-button ${isUVOn ? 'active' : ''}`}
                onClick={toggleUV}
              >
                <div className="button-icon">
                  {isUVOn ? '‚ö°' : 'üí°'}
                </div>
                <span>{isUVOn ? 'Disable UV' : 'Enable UV'}</span>
              </button>

              <div className="intensity-control">
                <label>UV Intensity</label>
                <input
                  type="range"
                  min="25"
                  max="100"
                  value={intensity}
                  onChange={(e) => setIntensity(e.target.value)}
                  className="intensity-slider"
                />
                <span className="intensity-value">{intensity}%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Information Panel */}
        <div className="info-panel">
          <div className="uv-info-card">
            <h3>UV Authentication Guide</h3>
            <div className="guide-content">
              <div className="guide-step">
                <div className="step-number">1</div>
                <div className="step-text">Position peso bill in camera view</div>
              </div>
              <div className="guide-step">
                <div className="step-number">2</div>
                <div className="step-text">Enable UV light simulation</div>
              </div>
              <div className="guide-step">
                <div className="step-number">3</div>
                <div className="step-text">Look for fluorescent security features</div>
              </div>
              <div className="guide-step">
                <div className="step-number">4</div>
                <div className="step-text">Verify thread continuity and patterns</div>
              </div>
            </div>
          </div>

          <div className="security-info-card">
            <h3>What to Look For</h3>
            <div className="security-points">
              <div className="security-point">
                <div className="point-icon">üßµ</div>
                <span>Security thread fluorescence</span>
              </div>
              <div className="security-point">
                <div className="point-icon">üîç</div>
                <span>Hidden text and patterns</span>
              </div>
              <div className="security-point">
                <div className="point-icon">üíß</div>
                <span>Watermark visibility</span>
              </div>
              <div className="security-point">
                <div className="point-icon">‚ú®</div>
                <span>Special ink reactions</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer Notice */}
      <footer className="uv-footer">
        <div className="notice">
          <div className="notice-icon">‚ìò</div>
          <div className="notice-text">
            <strong>Professional Note:</strong> This simulation demonstrates UV-reactive features. 
            For authentic verification, use calibrated UV equipment under controlled conditions.
          </div>
        </div>
      </footer>
    </div>
  );
};

export default UVLight;
