import React, { useState, useRef, useCallback } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import Webcam from 'react-webcam';
import { scanImage, uploadImage, comprehensiveScan } from '../services/api';
import Magnifier from '../components/Magnifier';
import './ScanPage.css';

const ScanPage = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [mode, setMode] = useState(searchParams.get('mode') || 'camera');
  const [scanType, setScanType] = useState('comprehensive'); // 'basic', 'comprehensive', or 'advanced' - Default to comprehensive for better counterfeit detection
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [capturedImage, setCapturedImage] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [magnifierEnabled, setMagnifierEnabled] = useState(false);

  const webcamRef = useRef(null);
  const fileInputRef = useRef(null);

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setCapturedImage(imageSrc);
    setPreviewUrl(imageSrc);
  }, [webcamRef]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
      setError('');
    }
  };

  const handleScan = async () => {
    setIsLoading(true);
    setError('');

    try {
      let result;
      let imageFile;

      if (mode === 'camera' && capturedImage) {
        const response = await fetch(capturedImage);
        imageFile = await response.blob();
      } else if (mode === 'upload' && uploadedFile) {
        imageFile = uploadedFile;
      } else {
        throw new Error('No image selected');
      }

      // Use comprehensive scan for better counterfeit detection by default
      if (scanType === 'comprehensive' || scanType === 'advanced') {
        result = await comprehensiveScan(imageFile);
      } else {
        // Basic scan for simple denomination detection
        if (mode === 'camera') {
          result = await scanImage(imageFile);
        } else {
          result = await uploadImage(imageFile);
        }
      }

      navigate('/results', { 
        state: { 
          result, 
          imageUrl: previewUrl,
          mode,
          scanType
        } 
      });
    } catch (err) {
      setError(err.message || 'Failed to process image');
    } finally {
      setIsLoading(false);
    }
  };

  const resetCapture = () => {
    setCapturedImage(null);
    setUploadedFile(null);
    setPreviewUrl('');
    setError('');
    setMagnifierEnabled(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="scan-page">
      <div className="container">
        <div className="scan-header">
          <h1>Scan Peso Bill</h1>
          <p>Choose your scanning method below</p>
        </div>

        <div className="mode-selector">
          <button
            className={`mode-btn ${mode === 'camera' ? 'active' : ''}`}
            onClick={() => {
              setMode('camera');
              resetCapture();
            }}
          >
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h4l2-3h6l2 3h4a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" fill="none"/>
              <circle cx="12" cy="13" r="4" stroke="currentColor" strokeWidth="2" fill="none"/>
            </svg>
            Camera Scan
          </button>

          <button
            className={`mode-btn ${mode === 'upload' ? 'active' : ''}`}
            onClick={() => {
              setMode('upload');
              resetCapture();
            }}
          >
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z" stroke="currentColor" strokeWidth="2" fill="none"/>
              <polyline points="14,2 14,8 20,8" stroke="currentColor" strokeWidth="2" fill="none"/>
              <line x1="16" y1="13" x2="8" y2="13" stroke="currentColor" strokeWidth="2"/>
              <line x1="16" y1="17" x2="8" y2="17" stroke="currentColor" strokeWidth="2"/>
            </svg>
            Upload Image
          </button>
        </div>

        <div className="scan-type-selector">
          <h3>Analysis Type</h3>
          <div className="scan-type-options">
            <label className={`scan-type-option ${scanType === 'basic' ? 'active' : ''}`}>
              <input
                type="radio"
                value="basic"
                checked={scanType === 'basic'}
                onChange={(e) => setScanType(e.target.value)}
              />
              <div className="option-content">
                <div className="option-icon"></div>
                <div className="option-text">
                  <h4>Basic Scan</h4>
                  <p>Detect peso denomination and basic features</p>
                  <small>Fast analysis for denomination identification</small>
                </div>
              </div>
            </label>

            <label className={`scan-type-option ${scanType === 'comprehensive' ? 'active' : ''}`}>
              <input
                type="radio"
                value="comprehensive"
                checked={scanType === 'comprehensive'}
                onChange={(e) => setScanType(e.target.value)}
              />
              <div className="option-content">
                <div className="option-icon"></div>
                <div className="option-text">
                  <h4>Comprehensive Analysis</h4>
                  <p>Full counterfeit detection with AI-powered security analysis</p>
                  <small>Advanced counterfeit detection using trained YOLOv8 model</small>
                </div>
              </div>
            </label>

            <label className={`scan-type-option ${scanType === 'advanced' ? 'active' : ''}`}>
              <input
                type="radio"
                value="advanced"
                checked={scanType === 'advanced'}
                onChange={(e) => setScanType(e.target.value)}
              />
              <div className="option-content">
                <div className="option-icon"></div>
                <div className="option-text">
                  <h4>Advanced Professional</h4>
                  <p>AR overlays, UV detection simulation, and BSP reporting</p>
                  <small>Professional tools for comprehensive authentication</small>
                </div>
                <div className="advanced-badges">
                  <span className="feature-badge ar">AR</span>
                  <span className="feature-badge uv">UV</span>
                  <span className="feature-badge bsp">BSP</span>
                  <span className="feature-badge magnifier">MAG</span>
                </div>
              </div>
            </label>
          </div>

          {scanType === 'comprehensive' && (
            <div className="comprehensive-info">
              <div className="info-box">
                <h4> Advanced Counterfeit Detection (93.99% Accuracy)</h4>
                <p>Our trained YOLOv8 model completed 100 epochs and analyzes 41+ security features:</p>
                <ul>
                  <li><strong>Security Features:</strong> Watermarks, security threads, optically variable devices</li>
                  <li><strong>Denomination Detection:</strong> All peso bills (20, 50, 100, 200, 500, 1000)</li>
                  <li><strong>Design Elements:</strong> Eagles, sampaguita, serial numbers, and specific patterns</li>
                  <li><strong>Physical Features:</strong> Clear windows, concealed values, see-through marks</li>
                </ul>
                <div className="model-stats">
                  <div className="stat-item">
                    <span className="stat-number">93.99%</span>
                    <span className="stat-label">mAP50 Accuracy</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-number">100</span>
                    <span className="stat-label">Training Epochs</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-number">41+</span>
                    <span className="stat-label">Security Features</span>
                  </div>
                </div>
                <p className="note">
                  <strong>Recommended:</strong> This analysis provides the most accurate counterfeit detection 
                  using our fully trained model.
                </p>
              </div>
            </div>
          )}

          {scanType === 'advanced' && (
            <div className="advanced-info">
              <div className="info-box advanced">
                <h4> Professional Authentication Suite</h4>
                <p>Advanced tools for professional-grade authentication:</p>
                <div className="feature-details">
                  <div className="feature-item">
                    <span className="feature-badge ar">AR</span>
                    <div>
                      <strong>AR Overlays:</strong> Real-time augmented reality highlighting of security features with color-coded detection boxes
                    </div>
                  </div>
                  <div className="feature-item">
                    <span className="feature-badge uv">UV</span>
                    <div>
                      <strong>UV Detection Simulation:</strong> Interactive ultraviolet light simulation showing UV-reactive security elements
                    </div>
                  </div>
                  <div className="feature-item">
                    <span className="feature-badge bsp">BSP</span>
                    <div>
                      <strong>BSP Reporting:</strong> Generate official reports with BSP contact information for suspicious bills
                    </div>
                  </div>
                  <div className="feature-item">
                    <span className="feature-badge magnifier">MAG</span>
                    <div>
                      <strong>Bill Magnifier:</strong> High-resolution magnification tool to examine security features in detail
                    </div>
                  </div>
                </div>
                <p className="note advanced">
                  <strong>Professional Mode:</strong> Designed for banking professionals and security experts 
                  requiring comprehensive authentication tools.
                </p>
              </div>
            </div>
          )}
        </div>

        <div className="scan-content">
          {mode === 'camera' ? (
            <div className="camera-section">
              {!capturedImage ? (
                <div className="webcam-container">
                  <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    videoConstraints={{
                      width: 640,
                      height: 480,
                      facingMode: "environment"
                    }}
                    className="webcam"
                  />
                  <div className="camera-overlay">
                    <div className="viewfinder">
                      <div className="corner top-left"></div>
                      <div className="corner top-right"></div>
                      <div className="corner bottom-left"></div>
                      <div className="corner bottom-right"></div>
                    </div>
                    <p className="camera-instruction">Position the peso bill within the frame</p>
                  </div>
                </div>
              ) : (
                <div className="preview-container">
                  <img src={capturedImage} alt="Captured" className="preview-image" />
                </div>
              )}

              <div className="camera-controls">
                {!capturedImage ? (
                  <button onClick={capture} className="btn btn-primary btn-large">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" fill="none"/>
                      <circle cx="12" cy="12" r="3" fill="currentColor"/>
                    </svg>
                    Capture Photo
                  </button>
                ) : (
                  <div className="captured-controls">
                    <button onClick={resetCapture} className="btn btn-outline">
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <polyline points="1,4 1,10 7,10" stroke="currentColor" strokeWidth="2" fill="none"/>
                        <path d="M3.51 15a9 9 0 1 0 2.13-9.36L1 10" stroke="currentColor" strokeWidth="2" fill="none"/>
                      </svg>
                      Retake
                    </button>
                    <button 
                      onClick={handleScan} 
                      className="btn btn-primary btn-large"
                      disabled={isLoading}
                    >
                      {isLoading ? (
                        <>
                          <div className="spinner"></div>
                          Processing...
                        </>
                      ) : (
                        <>
                          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <circle cx="11" cy="11" r="8" stroke="currentColor" strokeWidth="2" fill="none"/>
                            <path d="M21 21l-4.35-4.35" stroke="currentColor" strokeWidth="2"/>
                          </svg>
                          Analyze Bill
                        </>
                      )}
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="upload-section">
              <div className="upload-area" onClick={() => fileInputRef.current?.click()}>
                {!uploadedFile ? (
                  <div className="upload-placeholder">
                    <svg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z" stroke="currentColor" strokeWidth="2" fill="none"/>
                      <polyline points="14,2 14,8 20,8" stroke="currentColor" strokeWidth="2" fill="none"/>
                      <line x1="12" y1="18" x2="12" y2="12" stroke="currentColor" strokeWidth="2"/>
                      <line x1="9" y1="15" x2="12" y2="12" stroke="currentColor" strokeWidth="2"/>
                      <line x1="15" y1="15" x2="12" y2="12" stroke="currentColor" strokeWidth="2"/>
                    </svg>
                    <h3>Upload Image</h3>
                    <p>Click to select or drag and drop an image</p>
                    <p className="file-types">Supports: JPG, PNG, JPEG</p>
                  </div>
                ) : (
                  <div className="upload-preview">
                    <img src={previewUrl} alt="Uploaded" className="preview-image" />
                    <div className="upload-info">
                      <p className="file-name">{uploadedFile.name}</p>
                      <p className="file-size">{(uploadedFile.size / 1024 / 1024).toFixed(2)} MB</p>
                    </div>
                  </div>
                )}
              </div>

              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleFileUpload}
                className="file-input"
                hidden
              />

              {uploadedFile && (
                <div className="upload-controls">
                  <button onClick={resetCapture} className="btn btn-outline">
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <polyline points="3,6 5,6 21,6" stroke="currentColor" strokeWidth="2" fill="none"/>
                      <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" stroke="currentColor" strokeWidth="2" fill="none"/>
                    </svg>
                    Remove
                  </button>
                  <button 
                    onClick={handleScan} 
                    className="btn btn-primary btn-large"
                    disabled={isLoading}
                  >
                    {isLoading ? (
                      <>
                        <div className="spinner"></div>
                        Processing...
                      </>
                    ) : (
                      <>
                        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <circle cx="11" cy="11" r="8" stroke="currentColor" strokeWidth="2" fill="none"/>
                          <path d="M21 21l-4.35-4.35" stroke="currentColor" strokeWidth="2"/>
                        </svg>
                        Analyze Bill
                      </>
                    )}
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Magnifier Component - Show when there's an image */}
          {previewUrl && (
            <Magnifier
              imageUrl={previewUrl}
              magnificationLevel={scanType === 'advanced' ? 4 : 3}
              magnifierSize={scanType === 'advanced' ? 200 : 180}
              isActive={magnifierEnabled}
              onToggle={() => setMagnifierEnabled(!magnifierEnabled)}
            />
          )}
        </div>

        {error && (
          <div className="error-message">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" fill="none"/>
              <line x1="15" y1="9" x2="9" y2="15" stroke="currentColor" strokeWidth="2"/>
              <line x1="9" y1="9" x2="15" y2="15" stroke="currentColor" strokeWidth="2"/>
            </svg>
            {error}
          </div>
        )}
      </div>
    </div>
  );
};

export default ScanPage;
