import React, { useState, useRef, useEffect } from 'react';
import './UVLight.css';

const UVLight = () => {
  const [isUVOn, setIsUVOn] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [cameraStream, setCameraStream] = useState(null);
  const [showGuide, setShowGuide] = useState(true);
  const videoRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const stopCameraCleanup = () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
    };

    startCamera();
    return stopCameraCleanup;
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment', // Use back camera
          width: { ideal: 1920 },
          height: { ideal: 1080 }
        }
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      setCameraStream(stream);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };



  const toggleUV = () => {
    setIsUVOn(!isUVOn);
  };

  const toggleFullscreen = () => {
    if (!isFullscreen) {
      if (containerRef.current.requestFullscreen) {
        containerRef.current.requestFullscreen();
      }
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      }
    }
    setIsFullscreen(!isFullscreen);
  };

  const pesoSecurityFeatures = [
    {
      denomination: '‚Ç±20',
      uvFeatures: [
        'Security thread glows blue',
        'Portrait watermark becomes visible',
        'Microprinting fluoresces'
      ]
    },
    {
      denomination: '‚Ç±50',
      uvFeatures: [
        'Security thread glows green',
        'Hidden denomination appears',
        'Special ink patterns visible'
      ]
    },
    {
      denomination: '‚Ç±100',
      uvFeatures: [
        'Security thread glows red',
        'UV-reactive fibers in paper',
        'Hidden text becomes visible'
      ]
    },
    {
      denomination: '‚Ç±500',
      uvFeatures: [
        'Security thread glows yellow',
        'Complex UV patterns',
        'Multiple fluorescent elements'
      ]
    },
    {
      denomination: '‚Ç±1000',
      uvFeatures: [
        'Security thread glows multi-color',
        'Advanced UV security features',
        'Intricate fluorescent designs'
      ]
    }
  ];

  return (
    <div className={`uv-light-container ${isFullscreen ? 'fullscreen' : ''}`} ref={containerRef}>
      <div className="uv-header">
        <h1>üî¶ UV Light Detection Tool</h1>
        <p>Use UV light to reveal hidden security features in Philippine peso bills</p>
      </div>

      <div className="uv-main-content">
        <div className="camera-section">
          <div className={`camera-container ${isUVOn ? 'uv-active' : ''}`}>
            <video
              ref={videoRef}
              autoPlay
              playsInline
              className="camera-feed"
            />
            
            {isUVOn && (
              <>
                <div className="uv-overlay"></div>
                <div className="uv-effect"></div>
                <div className="scanning-grid"></div>
              </>
            )}

            <div className="camera-controls">
              <button
                className={`uv-toggle ${isUVOn ? 'active' : ''}`}
                onClick={toggleUV}
              >
                {isUVOn ? 'üî¶ UV ON' : 'üí° UV OFF'}
              </button>
              
              <button
                className="fullscreen-btn"
                onClick={toggleFullscreen}
              >
                {isFullscreen ? 'üóó Exit Fullscreen' : 'üóñ Fullscreen'}
              </button>
            </div>

            {showGuide && (
              <div className="guide-overlay">
                <div className="guide-content">
                  <h3>üìã How to Use UV Light Detection</h3>
                  <ol>
                    <li>Position the peso bill in front of the camera</li>
                    <li>Click "UV ON" to activate UV simulation</li>
                    <li>Look for glowing security features</li>
                    <li>Compare with the reference guide below</li>
                  </ol>
                  <button onClick={() => setShowGuide(false)}>Got it!</button>
                </div>
              </div>
            )}
          </div>

          <div className="uv-status">
            <div className={`status-indicator ${isUVOn ? 'active' : ''}`}>
              <span className="status-dot"></span>
              {isUVOn ? 'UV Light Active' : 'UV Light Inactive'}
            </div>
            {isUVOn && (
              <div className="detection-info">
                <p>üîç Scanning for UV-reactive security features...</p>
                <p>üí° Authentic peso bills will show specific UV patterns</p>
              </div>
            )}
          </div>
        </div>

        <div className="reference-guide">
          <h2>üìñ UV Security Features Reference</h2>
          <div className="features-grid">
            {pesoSecurityFeatures.map((feature, index) => (
              <div key={index} className="feature-card">
                <div className="denomination-header">
                  <h3>{feature.denomination}</h3>
                </div>
                <ul className="uv-features-list">
                  {feature.uvFeatures.map((uvFeature, i) => (
                    <li key={i}>
                      <span className="feature-indicator">‚ú®</span>
                      {uvFeature}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="uv-footer">
        <div className="warning-notice">
          <h4>‚ö†Ô∏è Important Notice</h4>
          <p>
            This is a simulation of UV light detection. For actual counterfeit detection, 
            use a real UV light source. This tool helps you understand what to look for 
            when using genuine UV detection equipment.
          </p>
        </div>
      </div>
    </div>
  );
};

export default UVLight;
