import React, { createContext, useContext, useState, useEffect } from 'react';

const DarkModeContext = createContext();

export const useDarkMode = () => {
  const context = useContext(DarkModeContext);
  if (!context) {
    throw new Error('useDarkMode must be used within a DarkModeProvider');
  }
  return context;
};

export const DarkModeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    // Check localStorage for saved preference
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    // ===================================================================
    // INSTANT THEME SWITCH - Works for BOTH dark-to-light AND light-to-dark
    // ===================================================================
    
    // STEP 1: Disable ALL transitions BEFORE changing theme (both directions)
    document.documentElement.style.setProperty('transition', 'none', 'important');
    document.body.style.setProperty('transition', 'none', 'important');
    
    // Force a reflow to ensure transitions are completely disabled
    void document.body.offsetHeight;
    
    // Save preference to localStorage
    localStorage.setItem('darkMode', JSON.stringify(isDarkMode));
    
    // STEP 2: Apply/remove theme classes INSTANTLY (no transitions active)
    if (isDarkMode) {
      // Switch to DARK mode
      document.documentElement.classList.add('dark-mode');
      document.body.classList.add('dark-mode');
    } else {
      // Switch to LIGHT mode
      document.documentElement.classList.remove('dark-mode');
      document.body.classList.remove('dark-mode');
    }
    
    // STEP 3: Re-enable transitions after theme is fully applied
    // This allows hover effects to work while keeping theme switch instant
    setTimeout(() => {
      document.documentElement.style.removeProperty('transition');
      document.body.style.removeProperty('transition');
    }, 50); // 50ms ensures DOM has fully painted with new colors
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(prev => !prev);
  };

  return (
    <DarkModeContext.Provider value={{ isDarkMode, toggleDarkMode }}>
      {children}
    </DarkModeContext.Provider>
  );
};
