                <div className="summary-item">
                  <span className="summary-label">Counterfeit Probability:</span>
                  <span className={`summary-value ${overallAssessment?.counterfeit_probability <= 0.4 ? 'positive' : 'negative'}`}>
                    {Math.round((overallAssessment?.counterfeit_probability || 0) * 100)}%
                  </span>
                </div>
              </div>
              
              <div className="analysis-recommendation">
                <h4>Recommendation</h4>
                <p className={`recommendation-text ${overallAssessment?.authenticity_score >= 0.6 ? 'positive' : 'negative'}`}>
                  {overallAssessment?.recommendation || 'Analysis incomplete'}
                </p>
              </div>
              
              {counterfeitAnalysis?.detected_features && counterfeitAnalysis.detected_features.length > 0 && (
                <div className="detected-features">
                  <h4>Detected Counterfeit Analysis Features</h4>
                  <div className="features-list">
                    {counterfeitAnalysis.detected_features.slice(0, 6).map((feature, index) => (
                      <div key={index} className="feature-badge">
                        <span className="feature-name">{feature.feature}</span>
                        <span className="feature-confidence">{Math.round(feature.confidence * 100)}%</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
              
              {counterfeitAnalysis?.recommendations && counterfeitAnalysis.recommendations.length > 0 && (
                <div className="analysis-recommendations">
                  <h4>Additional Recommendations</h4>
                  <ul className="recommendations-list">
                    {counterfeitAnalysis.recommendations.map((rec, index) => (
                      <li key={index}>{rec}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          {/* Hybrid CNN Analysis Section */}
          {result.result?.hybrid_detection && (
            <div className="hybrid-cnn-section">
              <h3>ðŸ§  Hybrid AI Analysis (CNN + YOLO)</h3>
              
              <div className="hybrid-summary">
                <div className="ai-models-overview">
                  <div className="model-card yolo-model">
                    <div className="model-header">
                      <span className="model-icon">ðŸ‘ï¸</span>
                      <span className="model-name">YOLO Detection</span>
                    </div>
                    <div className="model-result">
                      <span className="model-denomination">
                        â‚±{result.result.hybrid_detection.yolo_prediction.denomination || 'N/A'}
                      </span>
                      <span className="model-confidence">
                        {Math.round((result.result.hybrid_detection.yolo_prediction.confidence || 0) * 100)}%
                      </span>
                    </div>
                  </div>

                  <div className="model-card cnn-model">
                    <div className="model-header">
                      <span className="model-icon">ðŸ§ </span>
                      <span className="model-name">CNN Classification</span>
                      <span className="model-architecture">ResNet50 + MobileNetV2</span>
                    </div>
                    <div className="model-result">
                      <span className="model-denomination">
                        â‚±{result.result.hybrid_detection.cnn_prediction.denomination || 'N/A'}
                      </span>
                      <span className="model-confidence">
                        {Math.round((result.result.hybrid_detection.cnn_prediction.confidence || 0) * 100)}%
                      </span>
                      <span className={`cnn-status ${result.result.hybrid_detection.cnn_prediction.available ? 'available' : 'unavailable'}`}>
                        {result.result.hybrid_detection.cnn_prediction.available ? 'âœ… Active' : 'âŒ Unavailable'}
                      </span>
                    </div>
                  </div>
                </div>

                <div className="ensemble-result">
                  <h4>ðŸ”€ Ensemble Prediction</h4>
                  <div className="ensemble-details">
                    <div className="ensemble-method">
                      <span className="method-label">Method:</span>
                      <span className="method-value">
                        {(() => {
                          const method = result.result.hybrid_detection.ensemble_method;
                          switch (method) {
                            case 'agreement': return 'âœ… Models Agree';
                            case 'cnn_priority': return 'ðŸ¥‡ CNN Priority (Higher Confidence)';
                            case 'yolo_priority': return 'ðŸ¥ˆ YOLO Priority (Higher Confidence)';
                            case 'cnn_only': return 'ðŸ§  CNN Only';
                            case 'yolo_only': return 'ðŸ‘ï¸ YOLO Only';
                            default: return method || 'Unknown';
                          }
                        })()}
                      </span>
                    </div>
                    <div className="final-prediction">
                      <span className="final-denomination">â‚±{result.result.hybrid_detection.final_denomination}</span>
                      <span className="final-confidence">{Math.round(result.result.hybrid_detection.final_confidence)}%</span>
                    </div>
                  </div>
                </div>

                <div className="hybrid-advantages">
                  <h4>ðŸš€ Hybrid AI Advantages</h4>
                  <div className="advantages-grid">
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸŽ¯</span>
                      <span className="advantage-text">Enhanced Accuracy through Ensemble Learning</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸ”</span>
                      <span className="advantage-text">YOLO: Real-time Object Detection</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸ§ </span>
                      <span className="advantage-text">CNN: Deep Feature Analysis (29.2M parameters)</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">âš¡</span>
                      <span className="advantage-text">Transfer Learning from ImageNet</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="results-actions">
          <button onClick={handleNewScan} className="btn btn-primary btn-large">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h4l2-3h6l2 3h4a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" fill="none"/>
              <circle cx="12" cy="13" r="4" stroke="currentColor" strokeWidth="2" fill="none"/>
            </svg>
            Scan Another Banknote
          </button>
          
          <button onClick={handleSaveToHistory} className="btn btn-secondary">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M19 21l-7-5-7 5V5a2 2 0 0 1 2-2h10a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" fill="none"/>
            </svg>
            Save to History
          </button>
        </div>

        <div className="disclaimer-section">
          <div className="disclaimer-card">
            <h4>Important Disclaimer</h4>
            <p>
              This system is designed for educational and research purposes. 
              While our AI model strives for high accuracy, it should not be used as the sole method 
              for currency authentication in critical financial decisions. 
              Always verify suspicious banknotes through official banking channels and trained professionals.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
