import React, { useState, useEffect } from 'react';
import './BSPTips.css';

const BSPTips = ({ denomination, scanResults }) => {
  const [tips, setTips] = useState([]);
  const [contacts, setContacts] = useState(null);
  const [activeCategory, setActiveCategory] = useState('security_features');
  const [isLoading, setIsLoading] = useState(true);
  const [randomTip, setRandomTip] = useState(null);

  const categories = [
    { id: 'security_features', name: 'Security Features', icon: 'üîí' },
    { id: 'counterfeit_detection', name: 'Counterfeit Detection', icon: 'üîç' },
    { id: 'bill_handling', name: 'Bill Handling', icon: 'üí≥' },
    { id: 'damage_prevention', name: 'Damage Prevention', icon: 'üõ°Ô∏è' },
    { id: 'bsp_services', name: 'BSP Services', icon: 'üèõÔ∏è' },
    { id: 'emergency_procedures', name: 'Emergency', icon: 'üö®' }
  ];

  const fetchTips = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:8000/api/bsp-tips?category=${activeCategory}&priority=3`);
      if (response.ok) {
        const data = await response.json();
        setTips(data.tips || []);
        
        // Set random tip if we don't have one yet
        if (!randomTip && data.tips.length > 0) {
          const random = data.tips[Math.floor(Math.random() * data.tips.length)];
          setRandomTip(random);
        }
      }
    } catch (error) {
      console.error('Error fetching BSP tips:', error);
      // Use fallback tips
      setTips(getFallbackTips(activeCategory));
    } finally {
      setIsLoading(false);
    }
  };

  const fetchContacts = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/bsp-contacts');
      if (response.ok) {
        const data = await response.json();
        setContacts(data.contacts);
      }
    } catch (error) {
      console.error('Error fetching BSP contacts:', error);
      setContacts(getFallbackContacts());
    }
  };

  // Initialize tips and contacts when component mounts or category changes
  useEffect(() => {
    fetchTips();
    fetchContacts();
  }, [activeCategory]); // eslint-disable-line react-hooks/exhaustive-deps

  const getFallbackTips = (category) => {
    const fallbackTips = {
      security_features: [
        {
          id: 'sf_001',
          title: 'Security Thread Authentication',
          content: 'Look for the security thread embedded in the paper. It should be visible as a dark line when held up to light.',
          priority: 5
        },
        {
          id: 'sf_002', 
          title: 'Watermark Verification',
          content: 'Hold the bill up to light to see the watermark portrait. It should match the portrait printed on the bill.',
          priority: 5
        }
      ],
      counterfeit_detection: [
        {
          id: 'cd_001',
          title: 'Feel the Texture',
          content: 'Genuine peso bills are printed on cotton-based paper with a distinct texture you can feel.',
          priority: 5
        }
      ],
      emergency_procedures: [
        {
          id: 'ep_001',
          title: 'Counterfeit Bill Reporting',
          content: 'If you suspect a counterfeit bill, report to BSP hotline (02) 8708-7087 immediately.',
          priority: 5
        }
      ]
    };
    
    return fallbackTips[category] || [];
  };

  const getFallbackContacts = () => {
    return {
      main_office: {
        phone: '(02) 8708-7087',
        email: 'consumeraffairs@bsp.gov.ph'
      },
      hotlines: {
        emergency: '117',
        currency_investigation: '(02) 8988-4833'
      }
    };
  };

  // Get contextual tips based on scan results
  const getContextualTips = () => {
    if (!scanResults) return [];
    
    const contextual = [];
    const confidence = scanResults.overall_assessment?.authenticity_score || scanResults.result?.confidence || 100;
    
    if (confidence < 70) {
      contextual.push({
        id: 'ctx_001',
        title: 'Low Confidence Detection',
        content: 'When scan results show low confidence, verify security features manually and consider seeking expert authentication.',
        priority: 5,
        contextual: true
      });
    }
    
    if (scanResults.counterfeit_analysis?.counterfeit_probability > 0.5) {
      contextual.push({
        id: 'ctx_002',
        title: 'Potential Counterfeit Detected',
        content: 'Do not accept or use this bill. Report to BSP immediately and preserve as evidence if safe to do so.',
        priority: 5,
        contextual: true
      });
    }
    
    return contextual;
  };

  const handleReportCounterfeit = () => {
    // This could open a modal or navigate to BSP reporting
    if (contacts?.hotlines?.currency_investigation) {
      window.open(`tel:${contacts.hotlines.currency_investigation}`);
    }
  };

  if (isLoading && tips.length === 0) {
    return (
      <div className="bsp-tips loading">
        <div className="loading-spinner"></div>
        <p>Loading BSP tips and guidelines...</p>
      </div>
    );
  }

  return (
    <div className="bsp-tips">
      {/* Header */}
      <div className="tips-header">
        <h3>üí° BSP Tips & Guidelines</h3>
        <p>Official guidance from Bangko Sentral ng Pilipinas</p>
      </div>

      {/* Random Daily Tip */}
      {randomTip && (
        <div className="daily-tip">
          <div className="tip-badge">üí° Daily Tip</div>
          <h4>{randomTip.title}</h4>
          <p>{randomTip.content}</p>
        </div>
      )}

      {/* Contextual Tips based on scan results */}
      {scanResults && getContextualTips().length > 0 && (
        <div className="contextual-tips">
          <h4>üéØ Based on Your Scan</h4>
          {getContextualTips().map(tip => (
            <div key={tip.id} className="contextual-tip alert">
              <h5>{tip.title}</h5>
              <p>{tip.content}</p>
            </div>
          ))}
        </div>
      )}

      {/* Category Tabs */}
      <div className="category-tabs">
        {categories.map(category => (
          <button
            key={category.id}
            className={`category-tab ${activeCategory === category.id ? 'active' : ''}`}
            onClick={() => setActiveCategory(category.id)}
          >
            <span className="category-icon">{category.icon}</span>
            <span className="category-name">{category.name}</span>
          </button>
        ))}
      </div>

      {/* Tips Content */}
      <div className="tips-content">
        {tips.length > 0 ? (
          <div className="tips-grid">
            {tips.map(tip => (
              <div key={tip.id} className="tip-card">
                <div className="tip-header">
                  <h5 className="tip-title">{tip.title}</h5>
                  <div className="tip-priority">
                    {'‚≠ê'.repeat(tip.priority)}
                  </div>
                </div>
                <p className="tip-content">{tip.content}</p>
                {tip.denomination && (
                  <div className="tip-denomination">
                    Specific to: ‚Ç±{tip.denomination}
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="no-tips">
            <p>No tips available for this category</p>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h4>üö® Quick Actions</h4>
        <div className="action-buttons">
          <button 
            className="action-btn report-btn"
            onClick={handleReportCounterfeit}
          >
            <span className="btn-icon">üìû</span>
            Report Counterfeit
          </button>
          
          {contacts?.main_office?.email && (
            <button 
              className="action-btn email-btn"
              onClick={() => window.open(`mailto:${contacts.main_office.email}`)}
            >
              <span className="btn-icon">‚úâÔ∏è</span>
              Email BSP
            </button>
          )}
          
          <button 
            className="action-btn website-btn"
            onClick={() => window.open('https://www.bsp.gov.ph', '_blank')}
          >
            <span className="btn-icon">üåê</span>
            BSP Website
          </button>
        </div>
      </div>

      {/* Contact Information */}
      {contacts && (
        <div className="contact-section">
          <h4>üìû BSP Contact Information</h4>
          <div className="contact-grid">
            {contacts.main_office && (
              <div className="contact-item">
                <h5>Main Office</h5>
                <p><strong>Phone:</strong> {contacts.main_office.phone}</p>
                <p><strong>Email:</strong> {contacts.main_office.email}</p>
              </div>
            )}
            
            {contacts.hotlines && (
              <div className="contact-item">
                <h5>Emergency Hotlines</h5>
                {contacts.hotlines.emergency && (
                  <p><strong>Emergency:</strong> {contacts.hotlines.emergency}</p>
                )}
                {contacts.hotlines.currency_investigation && (
                  <p><strong>Currency Investigation:</strong> {contacts.hotlines.currency_investigation}</p>
                )}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Educational Resources */}
      <div className="educational-resources">
        <h4>üìö Educational Resources</h4>
        <div className="resource-links">
          <a 
            href="https://www.bsp.gov.ph/Currency/Banknotes.pdf" 
            target="_blank" 
            rel="noopener noreferrer"
            className="resource-link"
          >
            <span className="resource-icon">üìÑ</span>
            Philippine Banknotes Guide
          </a>
          <a 
            href="https://www.bsp.gov.ph/Currency/Security-Features.pdf" 
            target="_blank" 
            rel="noopener noreferrer"
            className="resource-link"
          >
            <span className="resource-icon">üîí</span>
            Security Features Manual
          </a>
          <a 
            href="https://www.bsp.gov.ph/Currency/Counterfeit-Detection.pdf" 
            target="_blank" 
            rel="noopener noreferrer"
            className="resource-link"
          >
            <span className="resource-icon">üîç</span>
            Counterfeit Detection Guide
          </a>
        </div>
      </div>
    </div>
  );
};

export default BSPTips;
