                        {result.result.hybrid_detection.cnn_prediction.available ? 'âœ… Active' : 'âŒ Unavailable'}
                      </span>
                    </div>
                  </div>
                </div>

                <div className="ensemble-result">
                  <h4>ðŸ”€ Ensemble Prediction</h4>
                  <div className="ensemble-details">
                    <div className="ensemble-method">
                      <span className="method-label">Method:</span>
                      <span className="method-value">
                        {(() => {
                          const method = result.result.hybrid_detection.ensemble_method;
                          switch (method) {
                            case 'agreement': return 'âœ… Models Agree';
                            case 'cnn_priority': return 'ðŸ¥‡ CNN Priority (Higher Confidence)';
                            case 'yolo_priority': return 'ðŸ¥ˆ YOLO Priority (Higher Confidence)';
                            case 'cnn_only': return 'ðŸ§  CNN Only';
                            case 'yolo_only': return 'ðŸ‘ï¸ YOLO Only';
                            default: return method || 'Unknown';
                          }
                        })()}
                      </span>
                    </div>
                    <div className="final-prediction">
                      <span className="final-denomination">â‚±{result.result.hybrid_detection.final_denomination}</span>
                      <span className="final-confidence">{Math.round(result.result.hybrid_detection.final_confidence)}%</span>
                    </div>
                  </div>
                </div>

                <div className="hybrid-advantages">
                  <h4>ðŸš€ Hybrid AI Advantages</h4>
                  <div className="advantages-grid">
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸŽ¯</span>
                      <span className="advantage-text">Enhanced Accuracy through Ensemble Learning</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸ”</span>
                      <span className="advantage-text">YOLO: Real-time Object Detection</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">ðŸ§ </span>
                      <span className="advantage-text">CNN: Deep Feature Analysis (29.2M parameters)</span>
                    </div>
                    <div className="advantage-item">
                      <span className="advantage-icon">âš¡</span>
                      <span className="advantage-text">Transfer Learning from ImageNet</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="results-actions">
          <button onClick={handleNewScan} className="btn btn-primary btn-large">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h4l2-3h6l2 3h4a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" fill="none"/>
              <circle cx="12" cy="13" r="4" stroke="currentColor" strokeWidth="2" fill="none"/>
            </svg>
            Scan Another Banknote
          </button>
          
          <button onClick={handleSaveToHistory} className="btn btn-secondary">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M19 21l-7-5-7 5V5a2 2 0 0 1 2-2h10a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" fill="none"/>
            </svg>
            Save to History
          </button>
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
