import React, { useState, useRef, useEffect } from 'react';
import './UVLight.css';

const UVLight = () => {
  const [isUVOn, setIsUVOn] = useState(false);
  const [cameraStream, setCameraStream] = useState(null);
  const [selectedDenomination, setSelectedDenomination] = useState('â‚±100');
  const [intensity, setIntensity] = useState(75);
  const videoRef = useRef(null);

  useEffect(() => {
    const stopCameraCleanup = () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
    };

    startCamera();
    return stopCameraCleanup;
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      setCameraStream(stream);
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  const toggleUV = () => {
    setIsUVOn(!isUVOn);
  };

  const securityFeatures = {
    'â‚±20': { color: '#6366f1', features: ['Security thread glows blue', 'Portrait watermark visible'] },
    'â‚±50': { color: '#10b981', features: ['Security thread glows green', 'Hidden denomination appears'] },
    'â‚±100': { color: '#ef4444', features: ['Security thread glows red', 'UV-reactive fibers visible'] },
    'â‚±200': { color: '#f59e0b', features: ['Security thread glows orange', 'Special ink patterns'] },
    'â‚±500': { color: '#eab308', features: ['Security thread glows yellow', 'Complex UV patterns'] },
    'â‚±1000': { color: '#8b5cf6', features: ['Multi-color security thread', 'Advanced UV features'] }
  };

  return (
    <div className="uv-container">
      {/* Header */}
      <header className="uv-header">
        <div className="header-content">
          <div className="title-section">
            <h1>UV Light Detection</h1>
            <p>Professional UV authentication for Philippine peso bills</p>
          </div>
          <div className="status-section">
            <div className={`uv-status ${isUVOn ? 'active' : 'inactive'}`}>
              <div className="status-indicator"></div>
              <span>{isUVOn ? 'UV Active' : 'UV Inactive'}</span>
            </div>
          </div>
        </div>
      </header>

      <div className="uv-main">
        {/* Camera Section */}
        <div className="camera-section">
          <div className={`camera-viewport ${isUVOn ? 'uv-mode' : ''}`}>
            <video
              ref={videoRef}
              autoPlay
              playsInline
              className="camera-feed"
            />
            
            {isUVOn && (
              <div className="uv-overlay">
                <div className="uv-filter"></div>
                <div className="scan-pattern"></div>
              </div>
            )}

            {/* Floating Controls */}
            <div className="camera-controls">
              <button
                className={`uv-button ${isUVOn ? 'active' : ''}`}
                onClick={toggleUV}
              >
                <div className="button-icon">
                  {isUVOn ? 'âš¡' : 'ðŸ’¡'}
                </div>
                <span>{isUVOn ? 'Disable UV' : 'Enable UV'}</span>
              </button>

              <div className="intensity-control">
                <label>UV Intensity</label>
                <input
                  type="range"
                  min="25"
                  max="100"
                  value={intensity}
                  onChange={(e) => setIntensity(e.target.value)}
                  className="intensity-slider"
                />
                <span className="intensity-value">{intensity}%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar with denomination selector and features */}
        <div className="info-sidebar">
          <div className="denomination-selector">
            <h3>Select Denomination</h3>
            <div className="denomination-grid">
              {Object.keys(securityFeatures).map((denom) => (
                <button
                  key={denom}
                  className={`denomination-btn ${selectedDenomination === denom ? 'active' : ''}`}
                  onClick={() => setSelectedDenomination(denom)}
                  style={{
                    '--accent-color': securityFeatures[denom].color
                  }}
                >
                  {denom}
                </button>
              ))}
            </div>
          </div>

          <div className="selected-features">
            <h3>Expected UV Features</h3>
            <div className="features-list">
              {securityFeatures[selectedDenomination].features.map((feature, index) => (
                <div key={index} className="feature-item">
                  <div 
                    className="feature-dot"
                    style={{ backgroundColor: securityFeatures[selectedDenomination].color }}
                  ></div>
                  <span>{feature}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="detection-tips">
            <h3>Detection Tips</h3>
            <div className="tip-list">
              <div className="tip">Position bill flat against dark background</div>
              <div className="tip">Look for consistent glow patterns</div>
              <div className="tip">Compare with reference images</div>
              <div className="tip">Check thread continuity</div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer Notice */}
      <footer className="uv-footer">
        <div className="notice">
          <div className="notice-icon">â“˜</div>
          <div className="notice-text">
            <strong>Professional Note:</strong> This simulation demonstrates UV-reactive features. 
            For authentic verification, use calibrated UV equipment under controlled conditions.
          </div>
        </div>
      </footer>
    </div>
  );
};

export default UVLight;
